import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'react-bootstrap';

import { LinkContainer } from 'react-router-bootstrap';

import NavigationBar from '../../components/NavigationBar'

import './styles.css';

function Question() {
  const [questions, setQuestions] = useState([
    {titleItem:'Que tipo de caminhoneiro você é?',idItem:1,questionId:1, question:'Carreta 3 eixos trucado é conhecida como?', option: [{opt:1, name: '12 rolamento', img: null},{opt:2, name: '06 rolamento', img: null},{opt:3, name: 'Ana Maria (AM)', img: null},{opt:4, name: '22 rolamento', img: null},{opt:5, name: 'Asa Dura', img: null},]},
    {titleItem:'Que tipo de caminhoneiro você é?',idItem:1,questionId:2, question:'Que música gosta de ouvir?',option: [{opt:1, name: 'Eletrônico', img: null},{opt:2, name: 'Forró', img: null},{opt:3, name: 'Sertanejo', img: null},{opt:4, name: 'Brega', img: null},]},
    {titleItem:'Que tipo de caminhoneiro você é?',idItem:1,questionId:3, question:'O que te faz feliz?', img: null, realizado: false},
    {titleItem:'Que tipo de caminhoneiro você é?',idItem:1,questionId:4, question:'Qual seu tipo de familia? ',img: null, realizado: true},]);
  const [i, setI] = useState(0);



  return ( 
    <>
    <NavigationBar/>

    <Container > 
     <Row>
      <Col>
        {`${i+1}. ` + questions[i].titleItem}
      </Col>
     </Row>
    </Container>   

    </>
  );
}

export default Question;
