import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'react-bootstrap';

import { LinkContainer } from 'react-router-bootstrap';

import NavigationBar from '../../components/NavigationBar'

import './styles.css';

function Quiz() {
  const [questions, setQuestions] = useState([]);
  const [i, setI] = useState(1);

  useEffect(() => {
    setQuestions([
      {idItem:1,questionId:1, question:'Carreta 3 eixos trucado é conhecida como?', option: [{opt:1, name: '12 rolamento', img: null},{opt:2, name: '06 rolamento', img: null},{opt:3, name: 'Ana Maria (AM)', img: null},{opt:4, name: '22 rolamento', img: null},{opt:5, name: 'Asa Dura', img: null},]},
      {idItem:1,questionId:2, question:'Que música gosta de ouvir?',option: [{opt:1, name: 'Eletrônico', img: null},{opt:2, name: 'Forró', img: null},{opt:3, name: 'Sertanejo', img: null},{opt:4, name: 'Brega', img: null},]},
      {idItem:1,questionId:3, question:'O que te faz feliz?', img: null, realizado: false},
      {idItem:1,questionId:4, question:'Qual seu tipo de familia? ',img: null, realizado: true},]);
  },[]);

  return ( 
    <>
    <NavigationBar/>

    <Container > 
     <Row>
      <Col>
        {questions[i].question}
      </Col>
     </Row>
    </Container>   

    </>
  );
}

export default Quiz;
